package Lec1;

public class Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 79;
		if (n >= 51) {
			System.out.println("Hey");
		}
		if (n >= 100) {
			System.out.println("Bklol");
		}
		if (n % 2 == 0) {// yes
			System.out.println("Even");
		} else {// no
			System.out.println("Odd");
		}
		// code

	}

}
