package Lec5;

public class Data_Types_Demo_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		System.out.println((int) (ch));
		System.out.println(ch++);// ch = (char)(ch + 1);
		System.out.println(ch);
		int x = 6;
		x++;// x = (int)(x + 1);
		ch = 'b';
		ch += 1;// ch = (char)(ch + 1);
		System.out.println(ch);
		x += 1;// x = (int)(x + 1);

	}

}
